def extract_value(text, start_marker, end_marker):
    try:
        start = text.find(start_marker)
        if start == -1:
            return None
        
        start += len(start_marker)
        end = text.find(end_marker, start)
        
        if end == -1:
            return None
        
        return text[start:end]
    except:
        return None

def spam_otp_codex(length):
    return ''.join(random.choices(string.ascii_letters + string.digits, k=length))

def spam_otp_fetch_value(response, start, end):
    try:
        idx = response.find(start)
        if idx == -1:
            return None
        
        idx += len(start)
        tail = response[idx:]
        end_idx = tail.find(end)
        
        if end_idx == -1:
            return None
        
        return tail[:end_idx]
    except:
        return None

