def utama():
    pilihan_sekarang = 0
    
    while True:
        tampilkan_menu(pilihan_sekarang)
        
        try:
            key = input()
            
            if key == '\x1b[A':  # Up arrow
                pilihan_sekarang = (pilihan_sekarang - 1) % len(daftar_menu)
            elif key == '\x1b[B':  # Down arrow
                pilihan_sekarang = (pilihan_sekarang + 1) % len(daftar_menu)
            elif key == '\r':  # Enter
                if pilihan_sekarang == 0:
                    spam_bot_telegram_main()
                elif pilihan_sekarang == 1:
                    spam_otp_main()
                elif pilihan_sekarang == 6:
                    chat_admin()
                elif pilihan_sekarang == 7:
                    tampilkan_cursor()
                    sys.exit(0)
        
        except EOFError:
            tampilkan_cursor()
            utama()
        except KeyboardInterrupt:
            tampilkan_cursor()
            utama()

